import React from "react";

// import social data
import { social } from "../data";


// import icon 
import { ImWhatsapp } from "react-icons/im";

const Footer = () => {
  return (
    <footer className="bg-black">
      <div className="container mx-auto">
        <div className="flex flex-col lg:flex-row mx-auto space-y-6 lg:space-y-0 items-center justify-between py-12">

          <div className="flex lg:w-1/1"> {/* Modificación de la clase w-1/2 por w-1/3 */}
            <div className="flex flex-col mx-5 md:mx-[100px]">
              <div className="flex space-x-6 items-center justify-center"> {/* Eliminación de la clase flex-col */}
                {social.map((item, index) => {
                  const { href, icon, name } = item;
                  return (
                    <a href={href} key={index} target="_blank" rel="noreferrer">
                      <div className="flex items-center text-center space-x-4 text-pink-300 text-sm">
                        <div className="mr-2">{icon}</div>
                        <p>{name}</p>
                      </div>
                    </a>
                  );
                })}
              </div>
            </div>
            <div className="flex flex-col mx-5 md:mx-10">
              <a href="https://wa.me/542494377713" className="ml-15 flex items-center text-green-400 text-lg">
                <ImWhatsapp className="mr-2"/>
                <p>WhatsApp</p>
              </a>
            </div>
          </div>
        </div>
      </div>
      <p className="text-center text-white w-full border-t border-pink-400 px-2 py-4">
        © 2023 | HTTP MARESCA NAHIR Desarrollo Web | Derechos Reservados.
      </p>
    </footer>
  );
};

export default Footer;
